-- Enable UUID extension
CREATE EXTENSION IF NOT EXISTS "uuid-ossp";

CREATE TABLE activity_logs (  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "description" text NOT NULL,\n  "created_at" timestamp with time zone DEFAULT now(),\n  "metadata" jsonb,\n  "unit_id" uuid,\n  "property_id" uuid,\n  "activity_type" character varying(100) NOT NULL,\n  "user_id" uuid,\n  "organization_id" uuid NOT NULL\n);
CREATE TABLE announcement_schedules (  "end_date" timestamp with time zone,\n  "time_of_day" time without time zone NOT NULL,\n  "next_run" timestamp with time zone NOT NULL,\n  "created_at" timestamp with time zone NOT NULL DEFAULT now(),\n  "updated_at" timestamp with time zone NOT NULL DEFAULT now(),\n  "repeat_frequency" text,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "announcement_id" uuid NOT NULL,\n  "start_date" timestamp with time zone NOT NULL,\n  "repeat_on" jsonb\n);
CREATE TABLE announcement_targets (  "updated_at" timestamp with time zone NOT NULL DEFAULT now(),\n  "target_type" text NOT NULL,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "announcement_id" uuid NOT NULL,\n  "target_id" uuid,\n  "created_at" timestamp with time zone NOT NULL DEFAULT now(),\n  "target_name" text\n);
CREATE TABLE announcement_types (  "name" text NOT NULL,\n  "organization_id" uuid NOT NULL,\n  "description" text,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "updated_at" timestamp with time zone NOT NULL DEFAULT now(),\n  "created_at" timestamp with time zone NOT NULL DEFAULT now()\n);
CREATE TABLE announcements (  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "status" text NOT NULL DEFAULT 'draft'::text,\n  "communication_method" ARRAY NOT NULL,\n  "content" text NOT NULL,\n  "title" text NOT NULL,\n  "property_id" uuid,\n  "is_scheduled" boolean NOT NULL DEFAULT false,\n  "issue_date" timestamp with time zone,\n  "author_id" uuid NOT NULL,\n  "announcement_type_id" uuid,\n  "organization_id" uuid NOT NULL,\n  "created_at" timestamp with time zone NOT NULL DEFAULT now(),\n  "updated_at" timestamp with time zone NOT NULL DEFAULT now()\n);
CREATE TABLE bank_account_types (  "description" text,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "is_predefined" boolean DEFAULT false,\n  "organization_id" uuid NOT NULL,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "name" character varying(100) NOT NULL\n);
CREATE TABLE bank_accounts (  "last_synced" timestamp with time zone,\n  "organization_id" uuid NOT NULL,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "account_number" character varying(255) NOT NULL,\n  "routing_number" character varying(50),\n  "bank_name" character varying(255) NOT NULL,\n  "status" character varying(50) DEFAULT 'active'::character varying,\n  "currency" character varying(10) DEFAULT 'USD'::character varying,\n  "is_default" boolean DEFAULT false,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "external_id" character varying(255),\n  "institution_id" character varying(255),\n  "metadata" jsonb,\n  "created_by" uuid NOT NULL,\n  "account_name" character varying(255) NOT NULL,\n  "balance_current" numeric(12,2) DEFAULT 0,\n  "balance_available" numeric(12,2) DEFAULT 0,\n  "account_type_id" uuid NOT NULL\n);
CREATE TABLE communication_logs (  "sender_id" uuid,\n  "content" text NOT NULL,\n  "status" text NOT NULL DEFAULT 'queued'::text,\n  "error_message" text,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "announcement_id" uuid,\n  "subject" text,\n  "recipient_id" uuid NOT NULL,\n  "sent_at" timestamp with time zone,\n  "delivered_at" timestamp with time zone,\n  "read_at" timestamp with time zone,\n  "organization_id" uuid NOT NULL,\n  "created_at" timestamp with time zone NOT NULL DEFAULT now(),\n  "updated_at" timestamp with time zone NOT NULL DEFAULT now(),\n  "message_type" text NOT NULL,\n  "recipient_type" text NOT NULL,\n  "method" text NOT NULL\n);
CREATE TABLE demo_requests (  "email" character varying(255) NOT NULL,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "full_name" character varying(255) NOT NULL,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "additional_comments" text,\n  "demo_preferences" text,\n  "country" character varying(100),\n  "company_size" character varying(50),\n  "industry" character varying(100),\n  "job_title" character varying(100),\n  "company_name" character varying(255) NOT NULL,\n  "phone" character varying(50)\n);
CREATE TABLE departments (  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "role_id" uuid,\n  "created_at" timestamp with time zone DEFAULT now(),\n  "updated_at" timestamp with time zone DEFAULT now(),\n  "name" character varying(100) NOT NULL,\n  "organization_id" uuid NOT NULL\n);
CREATE TABLE documents (  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "organization_id" uuid,\n  "related_to_id" uuid NOT NULL,\n  "uploaded_by" uuid,\n  "created_at" timestamp without time zone DEFAULT now(),\n  "updated_at" timestamp without time zone DEFAULT now(),\n  "related_to_type" character varying(50) NOT NULL,\n  "document_url" text NOT NULL,\n  "document_name" character varying(255) NOT NULL,\n  "document_type" character varying(100) NOT NULL\n);
CREATE TABLE expenses (  "amount" numeric(12,2) NOT NULL,\n  "expense_date" date NOT NULL,\n  "organization_id" uuid NOT NULL,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "status" character varying(50) DEFAULT 'recorded'::character varying,\n  "payee" character varying(255) NOT NULL,\n  "description" text,\n  "receipt_url" text,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "created_by" uuid NOT NULL,\n  "unit_id" uuid,\n  "property_id" uuid,\n  "payment_method_id" uuid,\n  "category_id" uuid NOT NULL\n);
CREATE TABLE financial_summaries (  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "period_start" date NOT NULL,\n  "upcoming_payables" numeric(12,2) DEFAULT 0,\n  "outstanding_invoices" numeric(12,2) DEFAULT 0,\n  "net_revenue" numeric(12,2),\n  "property_id" uuid,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "total_expenses" numeric(12,2) DEFAULT 0,\n  "total_income" numeric(12,2) DEFAULT 0,\n  "organization_id" uuid NOT NULL,\n  "period_end" date NOT NULL,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP\n);
CREATE TABLE invoice_items (  "tax_rate" numeric(5,2) DEFAULT 0,\n  "tax_amount" numeric(12,2) DEFAULT 0,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "description" text NOT NULL,\n  "unit_price" numeric(12,2) NOT NULL,\n  "amount" numeric(12,2) NOT NULL,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "invoice_id" uuid NOT NULL,\n  "quantity" numeric(10,2) NOT NULL DEFAULT 1\n);
CREATE TABLE invoice_payments (  "payment_id" uuid NOT NULL,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "amount_applied" numeric(12,2) NOT NULL,\n  "invoice_id" uuid NOT NULL,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid()\n);
CREATE TABLE invoices (  "pdf_url" text,\n  "due_date" date NOT NULL,\n  "invoice_number" character varying(50) NOT NULL,\n  "status" character varying(50) NOT NULL DEFAULT 'draft'::character varying,\n  "notes" text,\n  "client_type" character varying(50) NOT NULL,\n  "client_name" character varying(255) NOT NULL,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "created_by" uuid NOT NULL,\n  "unit_id" uuid,\n  "property_id" uuid,\n  "amount_due" numeric(12,2),\n  "amount_paid" numeric(12,2) DEFAULT 0,\n  "amount_total" numeric(12,2) NOT NULL,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "organization_id" uuid NOT NULL,\n  "client_id" uuid,\n  "issue_date" date NOT NULL\n);
CREATE TABLE lease_addendums (  "description" text,\n  "lease_id" uuid NOT NULL,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "document_url" text,\n  "title" character varying(255) NOT NULL,\n  "effective_date" date NOT NULL,\n  "created_by" uuid,\n  "created_at" timestamp without time zone DEFAULT now(),\n  "updated_at" timestamp without time zone DEFAULT now()\n);
CREATE TABLE lease_charges (  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "lease_id" uuid NOT NULL,\n  "amount" numeric(12,2) NOT NULL,\n  "charge_status" character varying(50) NOT NULL DEFAULT 'pending'::character varying,\n  "created_at" timestamp without time zone DEFAULT now(),\n  "description" text NOT NULL,\n  "type" character varying(50) NOT NULL,\n  "updated_at" timestamp without time zone DEFAULT now()\n);
CREATE TABLE lease_documents (  "document_status" character varying(50) NOT NULL DEFAULT 'Not Signed'::character varying,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "lease_id" uuid NOT NULL,\n  "created_at" timestamp without time zone NOT NULL DEFAULT now(),\n  "updated_at" timestamp without time zone NOT NULL DEFAULT now(),\n  "document_url" text NOT NULL\n);
CREATE TABLE lease_renewals (  "created_at" timestamp without time zone DEFAULT now(),\n  "updated_at" timestamp without time zone DEFAULT now(),\n  "renewal_term" integer(32,0),\n  "status" character varying(50) DEFAULT 'pending'::character varying,\n  "notes" text,\n  "rent_change" numeric,\n  "original_lease_id" uuid NOT NULL,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "renewal_date" date NOT NULL,\n  "new_lease_id" uuid\n);
CREATE TABLE leases (  "lease_issuer_id" uuid NOT NULL,\n  "payment_status" character varying(50) NOT NULL DEFAULT 'pending'::character varying,\n  "security_deposit_status" character varying(50) NOT NULL DEFAULT 'pending'::character varying,\n  "document_status" character varying(50) DEFAULT 'Not signed'::character varying,\n  "lease_terms" text,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "unit_id" uuid NOT NULL,\n  "start_date" date NOT NULL DEFAULT now(),\n  "tenant_id" uuid NOT NULL,\n  "end_date" date,\n  "rent_amount" numeric(12,2) NOT NULL,\n  "security_deposit" numeric(10,2),\n  "created_at" timestamp without time zone DEFAULT now(),\n  "updated_at" timestamp without time zone DEFAULT now(),\n  "last_payment_date" date,\n  "next_payment_date" date,\n  "payment_date" integer(32,0),\n  "is_auto_renew" boolean DEFAULT false,\n  "notice_period_days" integer(32,0) DEFAULT 30,\n  "late_fee_amount" numeric DEFAULT 0,\n  "status" character varying(50) DEFAULT 'Pending'::character varying,\n  "late_fee_days" integer(32,0) DEFAULT 5,\n  "roll_over_to_month_to_month" boolean NOT NULL DEFAULT false,\n  "payment_frequency" text NOT NULL DEFAULT 'Monthly'::text,\n  "rent_payment_status" character varying(50) NOT NULL DEFAULT 'pending'::character varying\n);
CREATE TABLE maintenance_comments (  "comment" text NOT NULL,\n  "ticket_id" uuid NOT NULL,\n  "updated_at" timestamp without time zone DEFAULT now(),\n  "created_at" timestamp without time zone DEFAULT now(),\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "commented_by" uuid\n);
CREATE TABLE maintenance_requests (  "completed_at" timestamp with time zone,\n  "tenant_id" uuid,\n  "unit_id" uuid,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "owner_id" uuid,\n  "related_to_id" uuid,\n  "scheduled_date" timestamp without time zone,\n  "property_id" uuid,\n  "maintenance_type_id" uuid,\n  "title" character varying(255) NOT NULL,\n  "description" text NOT NULL,\n  "priority" character varying(50) NOT NULL,\n  "status" character varying(50) DEFAULT 'pending'::character varying,\n  "related_to_type" character varying(50),\n  "maintenance_type" character varying(100),\n  "due_date" timestamp without time zone,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "assigned_to" uuid\n);
CREATE TABLE maintenance_ticket_history (  "change_description" text NOT NULL,\n  "created_at" timestamp without time zone DEFAULT now(),\n  "changed_by" uuid,\n  "ticket_id" uuid NOT NULL,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "new_status" character varying(50),\n  "previous_status" character varying(50)\n);
CREATE TABLE maintenance_types (  "organization_id" uuid NOT NULL,\n  "estimated_resolution_time" integer(32,0),\n  "is_emergency" boolean DEFAULT false,\n  "created_at" timestamp without time zone DEFAULT now(),\n  "updated_at" timestamp without time zone DEFAULT now(),\n  "description" text,\n  "name" character varying(100) NOT NULL,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4()\n);
CREATE TABLE notifications (  "type" character varying(50) NOT NULL,\n  "read" boolean DEFAULT false,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "title" character varying(255) NOT NULL,\n  "user_id" uuid,\n  "message" text NOT NULL\n);
CREATE TABLE organization_invitations (  "invited_by" uuid NOT NULL,\n  "email" character varying(255) NOT NULL,\n  "updated_at" timestamp with time zone DEFAULT now(),\n  "created_at" timestamp with time zone DEFAULT now(),\n  "expires_at" timestamp with time zone NOT NULL,\n  "role_id" uuid NOT NULL,\n  "organization_id" uuid NOT NULL,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "status" character varying(20) DEFAULT 'pending'::character varying,\n  "token" character varying(255)\n);
CREATE TABLE organizations (  "date_format" character varying(20) DEFAULT 'YYYY-MM-DD'::character varying,\n  "active" boolean DEFAULT true,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "tax_id" character varying(50),\n  "name" character varying(255) NOT NULL,\n  "billing_cycle" character varying(20),\n  "subscription_plan" character varying(50),\n  "subscription_status" character varying(50) DEFAULT 'inactive'::character varying,\n  "timezone" character varying(50) DEFAULT 'UTC'::character varying,\n  "logo_url" text,\n  "website" character varying(255),\n  "email" character varying(255),\n  "phone" character varying(50),\n  "currency" character varying(10) DEFAULT 'USD'::character varying,\n  "billing_address" text\n);
CREATE TABLE owner_properties (  "owner_id" uuid NOT NULL,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "property_id" uuid NOT NULL\n);
CREATE TABLE owners (  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "organization_id" uuid NOT NULL,\n  "bank_account_id" uuid,\n  "updated_at" timestamp without time zone DEFAULT now(),\n  "created_at" timestamp without time zone DEFAULT now(),\n  "user_id" uuid,\n  "status" character varying(50) DEFAULT 'active'::character varying,\n  "phone" character varying(50),\n  "notes" text,\n  "payment_schedule" character varying(50) DEFAULT 'monthly'::character varying,\n  "taxpayer_id" character varying(50),\n  "payment_method" character varying(50),\n  "address" text,\n  "email" character varying(255),\n  "owner_type" character varying(50),\n  "tax_id" character varying(50),\n  "company_name" character varying(255),\n  "last_name" character varying(100),\n  "first_name" character varying(100)\n);
CREATE TABLE payment_categories (  "description" text,\n  "organization_id" uuid NOT NULL,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "name" character varying(100) NOT NULL,\n  "is_predefined" boolean DEFAULT false\n);
CREATE TABLE payment_methods (  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "description" text,\n  "name" character varying(100) NOT NULL,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "organization_id" uuid NOT NULL,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid()\n);
CREATE TABLE payment_schedules (  "last_run_date" date,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "organization_id" uuid NOT NULL,\n  "lease_id" uuid,\n  "bank_account_id" uuid NOT NULL,\n  "amount" numeric(10,2) NOT NULL,\n  "start_date" date NOT NULL,\n  "end_date" date,\n  "next_schedule_date" date NOT NULL,\n  "day_of_month" integer(32,0),\n  "active" boolean DEFAULT true,\n  "category_id" uuid,\n  "created_by" uuid NOT NULL,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "description" text,\n  "frequency" character varying(50) NOT NULL\n);
CREATE TABLE payment_transactions (  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "gateway_response" jsonb,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "organization_id" uuid NOT NULL,\n  "amount" numeric(10,2) NOT NULL,\n  "transaction_date" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "payment_id" uuid NOT NULL,\n  "external_id" character varying(255),\n  "status" character varying(50) DEFAULT 'pending'::character varying\n);
CREATE TABLE payments (  "payment_date" date NOT NULL,\n  "amount" numeric(12,2) NOT NULL,\n  "lease_id" uuid,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "created_by" uuid,\n  "recipient_id" uuid,\n  "is_offline" boolean DEFAULT false,\n  "bank_account_id" uuid,\n  "invoice_id" uuid,\n  "category_id" uuid,\n  "payment_method_id" uuid,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "next_scheduled_date" date,\n  "recipient_type" character varying(50),\n  "payment_type" character varying(50) DEFAULT 'one-time'::character varying,\n  "transaction_id" character varying(255),\n  "status" character varying(50) DEFAULT 'pending'::character varying,\n  "organization_id" uuid NOT NULL,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP\n);
CREATE TABLE properties (  "address" text NOT NULL,\n  "last_activity_date" timestamp with time zone,\n  "active_leases" integer(32,0) DEFAULT 0,\n  "occupancy_rate" numeric DEFAULT 0,\n  "monthly_revenue" numeric DEFAULT 0,\n  "updated_at" timestamp without time zone DEFAULT now(),\n  "created_at" timestamp without time zone DEFAULT now(),\n  "organization_id" uuid NOT NULL,\n  "owner_id" uuid,\n  "total_units" integer(32,0) NOT NULL,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "zip_code" character varying(20) NOT NULL,\n  "state" character varying(100) NOT NULL,\n  "city" character varying(100) NOT NULL,\n  "name" character varying(255) NOT NULL,\n  "property_type" character varying(50) NOT NULL DEFAULT 'residential'::character varying,\n  "status" character varying(50) DEFAULT 'active'::character varying\n);
CREATE TABLE property_images (  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "image_url" text NOT NULL,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "property_id" uuid NOT NULL\n);
CREATE TABLE property_inspections (  "notes" text,\n  "status" text NOT NULL DEFAULT 'scheduled'::text,\n  "inspector_id" uuid,\n  "inspection_date" timestamp with time zone NOT NULL,\n  "unit_id" uuid,\n  "property_id" uuid,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "report_url" text,\n  "organization_id" uuid NOT NULL,\n  "updated_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP,\n  "created_at" timestamp with time zone DEFAULT CURRENT_TIMESTAMP\n);
CREATE TABLE property_managers (  "organization_id" uuid,\n  "updated_at" timestamp without time zone DEFAULT now(),\n  "created_at" timestamp without time zone DEFAULT now(),\n  "assigned_properties" ARRAY DEFAULT '{}'::uuid[],\n  "user_id" uuid,\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4()\n);
CREATE TABLE property_metrics (  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "created_at" timestamp with time zone DEFAULT now(),\n  "total_expenses" numeric(12,2) DEFAULT 0,\n  "outstanding_rent" numeric DEFAULT 0,\n  "collected_rent" numeric DEFAULT 0,\n  "net_revenue" numeric(12,2),\n  "outstanding_invoices" numeric(12,2) DEFAULT 0,\n  "total_income" numeric(12,2) DEFAULT 0,\n  "operational_costs" numeric DEFAULT 0,\n  "maintenance_costs" numeric DEFAULT 0,\n  "active_leases" integer(32,0) DEFAULT 0,\n  "occupancy_rate" numeric DEFAULT 0,\n  "monthly_revenue" numeric DEFAULT 0,\n  "metric_date" date NOT NULL,\n  "property_id" uuid NOT NULL\n);
CREATE TABLE property_stakeholders (  "ownership_percentage" numeric DEFAULT 100,\n  "is_primary" boolean DEFAULT false,\n  "notes" text,\n  "start_date" date NOT NULL,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "property_id" uuid NOT NULL,\n  "updated_at" timestamp with time zone DEFAULT now(),\n  "user_id" uuid NOT NULL,\n  "created_at" timestamp with time zone DEFAULT now(),\n  "end_date" date,\n  "stakeholder_type" character varying(50) NOT NULL\n);
CREATE TABLE rental_application_documents (  "uploaded_at" timestamp with time zone DEFAULT now(),\n  "uploaded_by" uuid NOT NULL,\n  "rental_application_id" uuid NOT NULL,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "file_name" text NOT NULL,\n  "file_path" text NOT NULL,\n  "file_type" text NOT NULL\n);
CREATE TABLE rental_applications (  "emergency_contact" jsonb,\n  "id_type" character varying(50),\n  "applicant_name" character varying(255) NOT NULL,\n  "applicant_email" character varying(255),\n  "applicant_phone_number" character varying(20),\n  "preferred_contact_method" ARRAY,\n  "organization_id" uuid NOT NULL,\n  "is_employed" boolean NOT NULL DEFAULT false,\n  "expiry_date" date,\n  "applicant_id" bigint(64,0) NOT NULL,\n  "updated_at" timestamp with time zone DEFAULT now(),\n  "created_at" timestamp with time zone DEFAULT now(),\n  "review_date" timestamp with time zone,\n  "status" character varying(50) DEFAULT 'pending'::character varying,\n  "reviewed_by" uuid,\n  "application_fee_paid" boolean DEFAULT false,\n  "previous_address" text,\n  "employment_info" jsonb,\n  "vehicle_details" jsonb,\n  "has_vehicles" boolean DEFAULT false,\n  "pet_details" jsonb,\n  "has_pets" boolean DEFAULT false,\n  "monthly_income" numeric,\n  "lease_term" integer(32,0),\n  "desired_move_in_date" date,\n  "application_date" timestamp with time zone DEFAULT now(),\n  "unit_id" uuid,\n  "property_id" uuid,\n  "id" uuid NOT NULL DEFAULT gen_random_uuid(),\n  "background_check_status" character varying(50),\n  "credit_check_status" character varying(50),\n  "rejection_reason" text,\n  "notes" text\n);
CREATE TABLE roles (  "permissions" jsonb,\n  "updated_at" timestamp with time zone DEFAULT now(),\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "name" character varying(50) NOT NULL,\n  "description" text,\n  "is_system_role" boolean DEFAULT false\n);
CREATE TABLE task_assignees (  "task_id" uuid NOT NULL,\n  "assigned_at" timestamp without time zone DEFAULT now(),\n  "user_id" uuid NOT NULL\n);
CREATE TABLE tasks (  "updated_at" timestamp without time zone DEFAULT now(),\n  "id" uuid NOT NULL DEFAULT uuid_generate_v4(),\n  "title" character varying(255) NOT NULL,\n  "description" text,\n  "priority" character varying(50) NOT NULL,\n  "status" character varying(50) NOT NULL DEFAULT 'pending'::character varying,\n  "created_at" timestamp without time zone DEFAULT now(),\n  "type" character varying(50),\n  "organization_id" uuid,\n  "due_date" timestamp without time zone,\n  "owner_id" uuid,\n  "related_to_id" uuid\n);
CREATE TABLE tasks_leases (  "lease_id" uuid NOT NULL,\n  "task_id" uuid NOT NULL\n);
CREATE TABLE tasks_properties (  "property_id" uuid NOT NULL,\n  "task_id" uuid NOT NULL\n);
CREATE TABLE tasks_related_tasks (  "task_id" uuid NOT NULL,\n  "related_task_id" uuid NOT NULL\n);
