CREATE OR REPLACE FUNCTION public.refresh_payment_statuses() RETURNS void AS $CREATE OR REPLACE FUNCTION public.refresh_payment_statuses()                                                                                                                                                        +
RETURNS void                                                                                                                                                                                                                                                                                     +
LANGUAGE plpgsql                                                                                                                                                                                                                                                                                 +
AS $function$                                                                                                                                                                                                                                                                                     +
